import React from "react";
import styled from "styled-components";
import MainVideo from "../assets/Walking Girl.mp4";
// cover video styled components

const VideoContainer = styled.section`
  width: 100%;
  height: 100vh;
  position: relative;

  video {
    width: 100%;
    height: 100vh;
    object-fit: cover;
    z-index: 0;
  }
`;

const DarkOverlay = styled.div`
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  z-index: 1;

  background-color: ${(props) => `rgba${props.theme.bodyRgba},0.6`};
`;

const CoverVideo = () => {
  return (
    <VideoContainer>
      <DarkOverlay />
      <video src={MainVideo} type="video/mp4" controls autoPlay muted loop />
    </VideoContainer>
  );
};

export default CoverVideo;
