import gsap from "gsap";
import ScrollTrigger from "gsap/ScrollTrigger";
import React from "react";
import { useLayoutEffect } from "react";
import { useRef } from "react";
import styled from "styled-components";

const Section = styled.section`
  min-height: 100vh;
  height: auto;
  width: 100%;
  margin: 0 auto;
  position: relative;
  overflow: hidden;

  display: flex;
  justify-content: flex-start;
  align-items: flex-start;
`;

const Title = styled.h1`
  font-size: ${(props) => props.theme.fontxxxl};
  font-family: "Kaushan Script";
  font-weight: 300;
  position: absolute;
  color: ${(props) => props.theme.text};
  text-shadow: 1px 1px 1px ${(props) => props.theme.body};
  top: 1rem;
  left: 5%;
  z-index: 15;
`;

const Left = styled.div`
  width: 35%;
  min-height: 100vh;
  background-color: ${(props) => props.theme.body};
  color: ${(props) => props.theme.text};

  position: fixed;
  left: 0;
  display: flex;
  justify-content: center;
  align-items: center;

  p {
    font-size: ${(props) => props.theme.fontlg};
    font-weight: 300;
    width: 80%;
    margin: 0 auto;
  }
`;

const Right = styled.div`
  position: absolute;
  padding-left: 30%;
  width: 65%;
  left: 35%;
  min-height: 100vh;
  background-color: ${(props) => props.theme.grey};

  display: flex;
  justify-content: flex-start;
  align-items: center;

  h2 {
    width: 5rem;
    margin: 0 2rem;
  }
`;

const Shop = () => {
  gsap.registerPlugin(ScrollTrigger);

  const ref = useRef(null);
  const horizontalRef = useRef(null);

  useLayoutEffect(() => {
    let element = ref.current;
    let scrollingElement = horizontalRef.current;
    let pinWrapWidth = scrollingElement.offsetWidth;

    let t1 = gsap.timeline();

    setTimeout(() => {
      t1.to(element, {
        scrollTrigger: {
          trigger: element,
          start: "top top",
          end: pinWrapWidth,
          scrub: true,
          pin: true,
          markers: true,
        },
        // we have to increase the scrolling height of this section same as the scrolling element width
        height: `${scrollingElement.scrollWidth}px`,
        ease: "none",
      });
    }, 1000);

    return () => {};
  }, []);
  return (
    <Section ref={ref}>
      <Title data-scroll data-scroll-speed="-1">
        New Collection
      </Title>

      <Left>
        <p>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Accusamus,
          laboriosam? Accusamus odio tenetur, numquam beatae laborum mollitia
          quod voluptates facere cupiditate ipsum ipsa dicta totam voluptatibus,
          <br />
          <br />
          minima, sit dolor velit similique corrupti. Amet id fugit corrupti!
          Voluptatibus dolor voluptatum doloremque veniam ratione, deserunt, qui
          ipsa est unde quidem corporis similique. Lorem ipsum dolor sit amet
          consectetur adipisicing elit. Id, praesentium.
        </p>
      </Left>

      <Right ref={horizontalRef}>
        <h1>img</h1>
        <h1>img</h1>
        <h1>img</h1>
        <h1>img</h1>
        <h1>img</h1>
        <h1>img</h1>
        <h1>img</h1>
      </Right>
    </Section>
  );
};

export default Shop;
